<script>
export default {
    data() {
        return {
             hoveredCity: '',
             id: ['台北市', '新北市', '基隆市', '桃園', '新竹', '宜蘭', '台中', '苗栗', '彰化', '南投', '雲林', '嘉義', '台南', '高雄', '屏東', '花蓮', '台東', '澎湖', '金門', '連江']
        };
    },
    methods: {
        showCity(id) {
          // 直接使用 SVG 的 id 當城市名稱
           this.hoveredCity = id;
          // 傳回emit事件給父層
           this.$emit('CityName', id);
        }
    }
}
</script>

